export type ParsedClaimInstruction<
  TProgram extends string = typeof DIVIDENDS_PROGRAM_ADDRESS,
  TAccountMetas extends readonly AccountMeta[] = readonly AccountMeta[],
> = {
  programAddress: Address<TProgram>;
  accounts: {
    /** The [MerkleDistributor]. */
    distributor: TAccountMetas[0];
    /** Status of the claim. */
    claimStatus: TAccountMetas[1];
    /** Distributor ATA containing the tokens to distribute. */
    from: TAccountMetas[2];
    /** Account to send the claimed tokens to. */
    to: TAccountMetas[3];
    /** Who is claiming the tokens. */
    claimant: TAccountMetas[4];
    /** Payer of the claim. */
    payer: TAccountMetas[5];
    mint: TAccountMetas[6];
    /** The [System] program. */
    systemProgram: TAccountMetas[7];
    /** SPL [Token] program. */
    tokenProgram: TAccountMetas[8];
  };
  data: ClaimInstructionData;
};

export function parseClaimInstruction<
  TProgram extends string,
  TAccountMetas extends readonly AccountMeta[],
>(
  instruction: Instruction<TProgram> &
    InstructionWithAccounts<TAccountMetas> &
    InstructionWithData<ReadonlyUint8Array>
): ParsedClaimInstruction<TProgram, TAccountMetas> {
  if (instruction.accounts.length < 9) {
    // TODO: Coded error.
    throw new Error('Not enough accounts');
  }
  let accountIndex = 0;
  const getNextAccount = () => {
    const accountMeta = (instruction.accounts as TAccountMetas)[accountIndex]!;
    accountIndex += 1;
    return accountMeta;
  };
  return {
    programAddress: instruction.programAddress,
    accounts: {
      distributor: getNextAccount(),
      claimStatus: getNextAccount(),
      from: getNextAccount(),
      to: getNextAccount(),
      claimant: getNextAccount(),
      payer: getNextAccount(),
      mint: getNextAccount(),
      systemProgram: getNextAccount(),
      tokenProgram: getNextAccount(),
    },
    data: getClaimInstructionDataDecoder().decode(instruction.data),
  };
}
